import { Interval } from 'src/app/models/utils/async-utils';
import * as i0 from "@angular/core";
import * as i1 from "./config.service";
export class EndpointsService {
    constructor(configService) {
        this.configService = configService;
        this.InitEndpoints();
    }
    async InitOptions() {
        this.apiOptions = await this.configService.loadOptions("APIOptions");
    }
    async InitEndpoints() {
        this.InitOptions();
        this.identityEndpoints = await this.configService.loadOptions("Endpoints:Identity");
        this.inventoryEndpoints = await this.configService.loadOptions("Endpoints:Inventory");
        this.itemEndpoints = await this.configService.loadOptions("Endpoints:Item");
        this.tradeEndpoints = await this.configService.loadOptions("Endpoints:Trade");
        this.walletEndpoints = await this.configService.loadOptions("Endpoints:Wallet");
    }
    async GetBasePath() {
        if (this.apiOptions == null) {
            await Interval(() => !this.apiOptions, 50, 5000);
            if (this.apiOptions) {
                return this.apiOptions.Url;
            }
            return null;
        }
        return this.apiOptions.Url;
    }
    // endpoints getters
    async GetIdentity() {
        await Interval(() => this.identityEndpoints == null, 20, 2000);
        return this.identityEndpoints;
    }
    async GetInventory() {
        await Interval(() => this.inventoryEndpoints == null, 20, 2000);
        return this.inventoryEndpoints;
    }
    async GetItem() {
        await Interval(() => this.itemEndpoints == null, 20, 2000);
        return this.itemEndpoints;
    }
    async GetTrade() {
        await Interval(() => this.tradeEndpoints == null, 20, 2000);
        return this.tradeEndpoints;
    }
    async GetWallet() {
        await Interval(() => this.walletEndpoints == null, 20, 2000);
        return this.walletEndpoints;
    }
}
